insert into restaurante (nome, cnpj, endereco, cep, descricao, tipo_de_cozinha_id) values ('Lahmajun - Delícias Turcas', '26.657.954/0001-65', 'Rua Doutor Penaforte Mendes, 171 - Bela Vista - Sao Paulo - SP', '01308-010', 'Restaurante de comidas típicas turcas e árabes, como lahmajun (pizza turca), pidé (esfiha gigante de 35cm), kebabs, esfiha e kibe. Pratos/lanches veganos (como falafel), vegetarianas, fazem parte dessa pequena rede de restaurante fast-food turco com três unidades.', 1);
insert into restaurante (nome, cnpj, endereco, cep, descricao, tipo_de_cozinha_id) values ('Almanara', '60.909.215/0001-51', 'R Basilio da Gama, 70 - Republica - Sao Paulo - SP', '01046-020', 'Quando se estabeleceu na cidade, em 1950, a culinária árabe ainda não fazia parte do dia a dia paulistano, ou das grandes metrópoles brasileiras, que nem hoje em dia. Com receitas originais da família fundadora, trazidas do Líbano, e uma deliciosa cozinha artesanal, a marca preserva a riqueza de sabores de uma culinária legítima e irresistível. No cardápio, opções como falafel e frango com arroz e amêndoas. No nosso cardápio você encontra as melhores esfihas, kibes e opções árabes de São Paulo', 1);
insert into restaurante (nome, cnpj, endereco, cep, descricao, tipo_de_cozinha_id) values ('Saj', '22.665.513/0001-54', 'Rua Carneiro da Cunha, 425 - Vila da Saude - Sao Paulo - SP', '41440-000', 'O Saj Restaurante, premiado e indicado por três anos consecutivos pela Veja SP, é conhecido por seus deliciosos pães e esfihas, bem como por seus pratos tradicionais e bem elaborados.', 1);
