insert into restaurante (nome, cnpj, endereco, cep, descricao, tipo_de_cozinha_id) values ('China in Box - Aclimação', '10.685.792/0001-39', 'Av. Aclimação, 279 - Aclimacao - Sao Paulo - SP', '01531-001', 'China in Box o maior delivery de comida chinesa entrega pratos feitos na hora do seu pedido, na caixinha mais conhecida do Brasil. Pratos tradicionais como yakissoba, frango xadrez, rolinho primavera e banana caramelada para pedir on-line. Peça agora!', 3);
