insert into restaurante (nome, cnpj, endereco, cep, descricao, tipo_de_cozinha_id) values ('Chinakim', '74.641.085/0001-23', 'Rua Humberto I, 94 - Vila Mariana - Sao Paulo - SP', '04018-030', 'Sejam bem-vindos! Desde 2006, o Chinakim oferece o melhor da culinária chinesa. Pratos feitos com ingredientes frescos e selecionados. Crescemos pelo marketing boca a boca. E graças aos nossos clientes, completamos 15 anos. Você que não conhece, nos dê a oportunidade e experimente o nosso cardápio.', 3);
