insert into restaurante (nome, cnpj, endereco, cep, descricao, tipo_de_cozinha_id) values ('Lellis Trattoria', '43.386.903/0001-65', 'Al Campinas, 1615 - Jardim Paulista - Sao Paulo - SP', '01404-003', 'No cardápio, deliciosas opções com sotaque italiano, que vão das carnes aos pescados, passando por assados, aves, massas e molhos especiais. Vale dizer: tudo é preparado com ingredientes da melhor qualidade. Entre as atrações, gnocchi de mandioquinha recheado com catupiry e manjericão, e o capeletti verdi. Opções de molho: 4 formaggi, à moda da casa, à romanesca, ao sugo, entre muitos outros.', 4);
